def open_ui(ui_name):
    global window, uis
    for p in multiprocessing.active_children():
        p.terminate()
    uis[ui_name].setupUi(window, uis)


class CheckProcess(QRunnable):
    signal = WorkerSignal()

    @pyqtSlot()
    def run(self):
        global breed_process, is_run
        while breed_process.is_alive() and is_run:
            self.signal.update_log.emit()
            time.sleep(0.1)
        if is_run:
            self.signal.finished.emit()


