def run_script(pokemon):
    if pokemon == "Eevee":
        # go to script folder to import main file and all dependencies
        sys.path.insert(0, "scripts/PokemonScripts/BreedFarming/EeveeBreed")
        import EeveeBreed
        EeveeBreed.run()
